import React, { Component } from 'react';
import { SaveBtn, ViewBtn, DeleteBtn } from '../components/Buttons';
import Jumbotron from '../components/Jumbotron';
import API from '../utils/API';
import { Link } from 'react-router-dom';
import { Col, Row, Container } from '../components/Grid';
import { List, ListItem } from '../components/List';
import { Input, FormBtn } from '../components/Form';

class SearchBooks extends Component {
	state = {
		books: [],
		title: '',
		authors: '',
		description: '',
		image: '',
		link: ''
	};

	componentDidMount() {
		this.loadBooks();
	}

	loadBooks = () => {
		API.getBooks(this.state.books).then((res) =>
			this.setState({
				books: res.data,
				title: '',
				author: '',
				description: '',
				image: '',
				link: ''
			}).catch((err) => console.log(err))
		);
	};

	deleteBook = (id) => {
		API.deleteBook(id).then((res) => this.loadBooks()).catch((err) => console.log(err));
	};

	handleInputChange = (event) => {
		const { name, value } = event.target;
		this.setState({
			[name]: value
		});
	};

	handleFormSubmit = (event) => {
		event.preventDefault();
		if (this.state.title) {
			API.getBooks({
				title: this.state.title,
				authors: this.state.authors,
				description: this.state.description,
				image: this.state.image,
				link: this.state.link
			})
				.then((res) => this.setState())
				.catch((err) => console.log(err));
		}
	};

	render() {
		return (
			<Container fluid>
				<Row>
					<Col size="md-6">
						<Jumbotron>
							<h1>Google Book Research</h1>
						</Jumbotron>
						<form>
							<Input
								value={this.state.title}
								onChange={this.handleInputChange}
								name="title"
								placeholder="Input your title here"
							/>
							<FormBtn disabled={!this.state.title} onClick={this.handleFormSubmit}>
								Submit Book
							</FormBtn>
						</form>
					</Col>
					<Col size="md-6 sm-12">
						<Jumbotron>
							<h1>Books On My List</h1>
						</Jumbotron>
						{this.state.books.length ? (
							<List>
								{this.state.books.map((book) => (
									<ListItem key={book._id}>
										<Link to={'/books/' + book._id}>
											<strong>
												{book.title} by {book.author}
											</strong>
										</Link>
										<DeleteBtn onClick={() => this.deleteBook(book._id)} />
									</ListItem>
								))}
							</List>
						) : (
							<h3>No Results to Display</h3>
						)}
					</Col>
				</Row>
			</Container>
		);
	}
}

export default SearchBooks;
